const cheerio = require('cheerio');
const html = items[0].json.body;
const $ = cheerio.load(html);
const keywords = ['project manager', 'business analyst', 'developer', 'automation'];
const jobs = [];
$('.jobs li').each((i, el) => {
  const title = $(el).find('.title').text().toLowerCase();
  const company = $(el).find('.company').text();
  const url = 'https://weworkremotely.com' + $(el).find('a').attr('href');
  if (keywords.some(k => title.includes(k))) {
    jobs.push({ title, company, url });
  }
});
return jobs.map(job => ({ json: job }));
